mapper.select:
select * from setmeal

    and category_id ={categoryId}
    and status=#{status}
    and name=#{name}

mapper.update:
update setmeal
category_id=#{categoryId}, name=#{name}, price=#{price}, status=#{status}, description=#{description}, image=#{image}, create_time=#{createTime}, create_user=#{createUser}, update_time=#{updateTime}, update_user=#{updateUser},
where id = #{id}

mapper.select:
select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select d.name as name, sd.copies as copies, d.image as image, d.description as description
from setmeal_dish sd join dish d on sd.dish_id = d.id
where sd.setmeal_id = #{setmealId}

select count(id) from setmeal

     and status = #{status} 
     and category_id = #{categoryId}
